    e.preventDefault();
    alert('Form submitted successfully');
      //  setFullName(name);
      //  setnewFullName(lastName);
  };
  
  
  

  return(
    <>
    <div className='main-div'>

   <form onSubmit={OnSubmit}>
     <h1> Hello {Fullname.fname} {Fullname.lname} </h1>
     <p> {Fullname.email} </p>
     <p> {Fullname.phone} </p> 
